<?php

// show error if PHP version < 5.3

if(PHP_MAJOR_VERSION < 5 || PHP_MINOR_VERSION < 3)
{
   echo '<div class="error">Watermelon wymaga do działania wersji PHP co najmniej 5.3.<br>Twój serwer posiada PHP w wersji ' . phpversion() . '.</div>';
   
   exit;
}

// show error if wmelon/ doesn't have write permissions

if(!is_writable('wmelon/'))
{
   echo '<div class="error">Folder <em>wmelon/</em> nie posiada wymaganych uprawnień do zapisu.<br>Na czas instalacji, nadaj mu w kliencie FTP uprawnienia <em>0777</em>, a następnie odśwież tą stronę.</div>';
   
   exit;
}

?>
<script>

// AJAX -- requesting unpacking script

if(window.XMLHttpRequest)
{
   req = new XMLHttpRequest()
}
else
{
   req = new ActiveXObject('Microsoft.XMLHTTP')
}

req.open('GET', 'wmelon/installer/unpack.php')
req.onreadystatechange = function(event)
{
   if(req.readyState == 4)
   {
      if(req.status == 200 && req.responseText == 'ok')
      {
         window.location = 'index.php'
      }
      else
      {
         document.body.innerHTML += 'Wystąpił błąd :('
         clearTimeout(timeoutID)
      }
   }
}

req.send(null);

// showing notice if it takes too long

timeoutID = setTimeout('showNotice()', 5000);

function showNotice()
{
   document.body.innerHTML += 'Czekaj...';
}

</script>